const back=document.querySelectorAll(".back"),items=document.querySelectorAll(".section-vision__item"),links=document.querySelectorAll(".menu__link");items.forEach(a=>{a.querySelector(".item__button").addEventListener("click",b=>{a.className.add("item--active")}),a.querySelector(".back").addEventListener("click",b=>{a.className.remove("item--active")})});const burger=document.getElementById("burger-menu"),page=document.getElementById("page"),body=document.body;function showSidebar(){document.getElementById("page__mask").addEventListener("click",closeSidebar),body.className.add("show-menu")}function closeSidebar(){body.className.remove("show-menu")}burger.addEventListener("click",()=>{body.className.contains("show-menu")?closeSidebar():showSidebar()});const menuLinks=document.querySelectorAll(".menu__link[data-goto]");if(menuLinks.length>0){menuLinks.forEach(b=>{b.addEventListener("click",a)});function a(b){closeSidebar();let a=b.target;if(a.dataset.goto&&document.querySelector(a.dataset.goto)){let c=document.querySelector(a.dataset.goto),d=c.getBoundingClientRect().top+pageYOffset;window.scrollTo({top:d,behavior:"smooth"}),b.preventDefault()}}}
